mapper.select:
    select 
    
    from T_BBS_BIG_SECTION
    
		    DEL_FLAG=#{delFlag}
		   
		    AND  BIG_SECTION_ID = #{bigSectionId}
		   
		   
		    AND (BIG_SECTION_ID LIKE '%'||#{res01}||'%' or BIG_SECTION_NAME LIKE '%'||#{res01}||'%' or BIG_SECTION_DESCRIPT LIKE '%'||#{res01}||'%')
		   
	
	
			
				ORDER BY ${page.orderBy}
			
			
				ORDER BY CJSJ DESC
