mapper.select:
select 

from T_BBS_FILE
where FILE_ID = #{fileId,jdbcType=VARCHAR} AND DEL_FLAG=${0}

select  
from(
 select 
 
 from T_BBS_FILE where DEL_FLAG=${0}
 AND RES02=${1}
 AND FILE_TYPE=${1}  order by CJSJ desc) WHERE ROWNUM <=${10}

    select 
    
    from T_BBS_FILE
    
    DEL_FLAG=${0}
    AND RES02=${1}
    AND FILE_TYPE=${1}
    
    
			
				ORDER BY ${page.orderBy}
			
			
				ORDER BY CJSJ DESC
