mapper.select:
    select 
    
    from T_BBS_SYSTEM_MESSAGE
    
    DEL_FLAG=${0}
    
	 AND (MESSAGE_NAMAE LIKE '%'||#{res01}||'%' or MESSAGE_CONTAINS LIKE '%'||#{res01}||'%' )
    
     
	 AND RES02= #{res02}
    
    
     
			
				ORDER BY ${page.orderBy}
			
			
				ORDER BY CJSJ DESC
